import React, { useState, useEffect } from "react";
import { useLocation } from 'react-router-dom';
import { getSiteImages, metaTags, getServerImage, getData } from '../../helpers/api';
import Cover from "../common/Cover";
import BlogPostsDetail from "./section/BlogPostsDetail";

function Blog() {
	let location = useLocation();
	let pathname = location.pathname;
	let url_arr = pathname.split("/");
	let blog_id = url_arr['2'];
	let ser_slug = url_arr['3'];
	const [loading, setLoading] = useState(false);
	const [state, setState] = useState({

	});
	useEffect(() => {

		getData("blog-details", blog_id + "/" + ser_slug).then((data) => {
			setState({
				...state,
				content: data.content,
				metatags: data.metatags,
			});
			setLoading(true);
		});

	}, []);
	let content = state.content;
	if (loading === false) return <div id="loading"> <img src={getSiteImages('/images/loading.gif')} alt="Loading" /></div>;
	const detail = {
		cover: {
			sec_bg: "/images/offering-20190301-digitaldna-hero.jpg",
			heading: "Blog Detail",
		},
		posts: {
			details: {
				title: "9 of the best professional biological examples we have ever seen",
				date: "28 February, 2022",
				src: "/images/Businesswoman hand working light.jpg",
			},
			category: {
				title: "Categories",
				ul: [
					{
						id: 1,
						li: "Science",
						link: "/blog/blog-detail",
					},
					{
						id: 2,
						li: "Information Technology",
						link: "/blog/blog-detail",
					},
					{
						id: 3,
						li: "Artificial Intelligence",
						link: "/blog/blog-detail",
					},
					{
						id: 4,
						li: "Craftsmanship",
						link: "/blog/blog-detail",
					},
					{
						id: 5,
						li: "Professional Beauty",
						link: "/blog/blog-detail",
					},
					{
						id: 6,
						li: "Agriculture",
						link: "/blog/blog-detail",
					},
				],
			},
		},
	};
	return (
		<>
			<Cover data={detail.cover} />
			<BlogPostsDetail data={detail.posts} />
		</>
	);
}

export default Blog;
