import React, { useState } from "react";
import { getSiteImages } from "../../helpers/api";
function Newsletter(props) {
	const [formVal, setFormVal] = useState({
		email: "",
	});
	const handleInput = (e) => {
		const name = e.target.name;
		const value = e.target.value;

		setFormVal({ ...formVal, [name]: value });
	};
	return (
		<>
			<div className="subscribe">
				<span className="fancy">{props.text}</span>
				<form action="" method="POST" onSubmit={handleInput}>
					<input type="text" name="email" id="email" value={formVal.email} onChange={handleInput} className="input" placeholder="Enter Email Address" />
					<button type="submit">
						<img src={getSiteImages("/images/icon-send.svg")} alt="Send" />
					</button>
				</form>
			</div>
		</>
	);
}

export default Newsletter;
